mapper.select:
select `log`.`id`, `log`.`suite_id`, `log`.`suite_log_no`, `log`.`run_time`, `log`.`total_run_case`,
`log`.`total_case`,`log`.`total_skip`,`log`.`execute_type`,`log`.`run_dev`,
`log`.`total_success`, `log`.`total_failed`, `log`.`total_error`, `log`.`start_time`, `log`.`end_time`,
`suite`.`suite_name`, `suite`.`desc` as `suite_desc`
from `t_interface_suite_log` as `log`
LEFT JOIN `t_interface_case_suite` as `suite`
on `log`.`suite_id` = `suite`.`suite_id`

    and `log`.`id`=#{id}
    and `log`.`suite_id`=#{suiteId}
    and `log`.`run_dev`=#{runDev}
    and `suite`.`suite_name` like concat('%',#{suiteName},'%')
    and `suite`.`desc` like concat('%',#{suiteDesc},'%')
    and `log`.`suite_log_no` like concat('%',#{suiteLogNo},'%')
    and `log`.`start_time`>=#{createdStartTime}
    and `log`.`start_time`<=#{createdEndTime}

order by `log`.`id` desc
